// --- Import built-in crypto ---
const crypto = require('crypto');

// --- Get form values ---
const title = $input.first().json.text; 
const html = $('Editor').first().json.output;
const status = "published";

// --- Build Ghost posts body ---
const body = {
  posts: [
    {
      title,
      html: String(html)
        .replace(/\\/g, "\\\\")
        .replace(/"/g, '\\"')
        .replace(/\r?\n/g, "\\n"),
      status
    }
  ]
};

// --- Get Ghost Admin Key from Form Trigger (format: "<id>:<secretHex>") ---
const key = $('n8n Form Trigger').first().json['ghost-admin-api-key'];
if (!key) {
  throw new Error("Missing ghost-admin-api-key from Form Trigger");
}

const [id, secretHex] = key.split(':');
if (!id || !secretHex) {
  throw new Error("Invalid GHOST_ADMIN_KEY format.");
}

// --- Helper for base64url ---
function base64url(input) {
  return Buffer.from(input)
    .toString('base64')
    .replace(/=/g, '')
    .replace(/\+/g, '-')
    .replace(/\//g, '_');
}

// --- JWT Header & Payload ---
const now = Math.floor(Date.now() / 1000);
const header = { alg: 'HS256', typ: 'JWT', kid: id };
const payload = { iat: now, exp: now + 60 * 60, aud: '/admin/' };

// --- Encode ---
const encodedHeader = base64url(JSON.stringify(header));
const encodedPayload = base64url(JSON.stringify(payload));
const signingInput = `${encodedHeader}.${encodedPayload}`;

// --- Sign with HMAC-SHA256 ---
const signature = crypto
  .createHmac('sha256', Buffer.from(secretHex, 'hex'))
  .update(signingInput)
  .digest();
const encodedSig = base64url(signature);

// --- Final JWT ---
const token = `${signingInput}.${encodedSig}`;

// --- Return both post body & token ---
return [{
  json: {
    token,
    body
  }
}];
